mapper.update:
update
			 member
	 set profile_url = #{profileURL}
	   , modify_date = sysdate
 where user_id = #{userId}

update
			 member
	 set user_name = #{userName}
	   , email = #{email}
	   , phone = #{phone}
	   , address = #{address}
	   , gender = #{gender}
	   , modify_date = sysdate
 where user_id = #{userId}

mapper.select:
SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE NICKNAME = #{checkNickName}

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE USER_ID = #{checkId}

