mapper.update:
update
			 member
	 set profile_url = #{profileURL}
	   , modify_date = sysdate
 where user_id = #{userId}

update
			 member
	 set user_name = #{userName}
	   , email = #{email}
	   , phone = #{phone}
	   , address = #{address}
	   , gender = #{gender}
	   , modify_date = sysdate
 where user_id = #{userId}

mapper.select:
SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE NICKNAME = #{checkNickName}

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE USER_ID = #{checkId}

mapper.insert:
  	INSERT
  		INTO
  			TB_MEMBER
  			(
  			USER_NO
			,	USER_ID
			,	USER_PWD
			,	USER_NAME
			,	PHONE
			,	BIRTH_DATE
			,	NICKNAME
			,	EMAIL
			,	ADDRESS
			,	ADDRESS_DETAIL
			, STREET_ADDRESS
			, OTHER_ADDERSS
  			)
  			VALUES
  			(
  			SEQ_MNO.NEXTVAL
  		, #{userId}
  		, #{userPwd}
  		, #{userName}
  		, #{phone}
  		, #{birthDate}
  		, #{nickName}
  		, #{email}
  		, #{address}
  		, #{addressDatail}
  		, #{streetAddress}
  		, #{otherAddress}
  			)

  	INSERT
  			INTO
  			 TB_MESSAGE_BOX
  			 (
  			   MESSAGE_NO
  			 , SEND_ID
  			 , RECEIVE_ID
  			 , CONTENT
  			 , TITLE
  			 , SEND_NICKNAME
  			 )
	  		VALUES
	  		(
	  		SEQ_MESNO.NEXTVAL
	  	, #{sendId}
	  	, #{receiveId}
	  	, #{content}
	  	, #{title}
	  	, #{sendNickName}
	  		)

        SELECT SEQ_MESNO.CURRVAL AS MESSAGE_NO FROM DUAL
